import React from 'react';
import { Sidebar, Segment, Menu, Icon, Header } from 'semantic-ui-react';
import './App.css';

class App extends React.Component {
  state = { visible: true }

  handleButtonClick = () => this.setState({ visible: !this.state.visible })

  render() {
    const { visible } = this.state;

    return (
      <div>
        <div className="ui inverted segment" style={{height: '50px', borderRadius: '0'}}>
          <Header as='h3' className="ui inverted header" style={{lineHeight: '50px'}}>My Header</Header>
        </div>

        <button onClick={this.handleButtonClick}>Toggle Sidebar</button>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            animation='overlay'
            icon='labeled'
            inverted
            onHide={() => this.setState({ visible: false })}
            vertical
            visible={visible}
            width='thin'
          >
            <Menu.Item as='a'>
              <Icon name='home' />
              Home
            </Menu.Item>
            <Menu.Item as='a'>
              <Icon name='gamepad' />
              Games
            </Menu.Item>
            <Menu.Item as='a'>
              <Icon name='camera' />
              Channels
            </Menu.Item>
          </Sidebar>

          <Sidebar.Pusher dimmed={visible} style={{ minHeight: '100vh' }}>
            <Segment basic>
              <Header as='h3'>Application Content</Header>
              <p>You can add your content here</p>
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div>
    )
  }
}

export default App;
